import React, {useState,useEffect} from 'react'

const Card = ({user}) => {
    const [listadecomentarios,set]=useState([])

    const [comment,setcomment]=useState(false)

    useEffect(() => {
        fetch('http://jsonplaceholder.typicode.com/comments')
        .then(res => res.json())
        .then((data) => {
          this.setState({ listadecomentarios: data })
        })
        set([])
    })
    return (
          <div class="card">
            <div class="card-body" onClick={()=> setcomment(!comment)}>
              <h5 class="card-title">{user.name}</h5>
              <h6 class="card-subtitle mb-2 text-muted">{user.email}</h6>
              {comment == true && <p class="card-text">{user.username}</p>}
              {comment == true && <div> lista</div>}
            </div>
          </div>
    )
}

export default Card
