import React from 'react'

const Card = ({user}) => {
    const [comment,setcomment]=useState(false)
    return (
          <div class="card">
            <div class="card-body" onCLick={()=> setcomment(true)}>
              <h5 class="card-title">{user.name}</h5>
              <h6 class="card-subtitle mb-2 text-muted">{user.email}</h6>
              {comment == true && <p class="card-text">{user.username}</p>}
            </div>
          </div>
    )
}

export default Card
